mapper.update:
update Counting
set totalcount=#{totalcount}, dailycount=#{dailycount}, weeklycount=#{weeklycount}, monthlycount=#{monthlycount}
where itemId = #{itemId}

UPDATE counting
set dailycount= 0
where countingId = #{countingId}

UPDATE counting
set weeklycount= 0
where countingId = #{countingId}

UPDATE counting
set monthlycount= 0
where countingId = #{countingId}
