mapper.update:
update Users
set username=#{username}, nickname=#{nickname}, phone=#{phone}, gender=#{gender}, profileImage=#{profileImage}, birthday=#{birthday}
where userId=#{userId}

update Users
set username=#{username}, password=#{password}, nickname=#{nickname}, phone=#{phone}, gender=#{gender}, profileImage=#{profileImage}
where userId=#{userId}

update Users
set roleId=0 
where userId=#{userId}
